function helpVadimSergeevich(str) {
    if (!str) return false;
    while (str.length > 0) {
        if (str.endsWith("1111")) {
            str = str.slice(0, -4);
        } else if (str.endsWith("711")) {
            str = str.slice(0, -3);
        } else if (str.endsWith("7")) {
            str = str.slice(0, -1);
        } else return false;
    }

    return str.length === 0;
}

console.log(
    helpVadimSergeevich(
        ""
    )
);
